  background-repeat: round;
  background-size: 300px;
}

.game-window {
  background-image: url("10e4345bfe7b75f039b8341dd8d4b28e.jpg");
  background-size: 100%;
  max-width: 1200px;
  max-height: 650px;
  min-width: 1050px;
  margin: 0 auto;
  box-shadow: 15px 35px 60px;
  border: 8px ridge #383838;
  display: grid;
  grid-template-columns: repeat(18, 1fr);
  grid-template-rows: repeat(9, 1fr);
}
.game-window #message-board {
  grid-column: 4/span 12;
  grid-row: 1/span 1;
}
.game-window #pot {
  grid-column: 4/span 12;
  grid-row: 2/span 4;
}
.game-window #ulf-runeboard {
